    useEffect(() => {
        const fetchData = async () => {
          try {
            const resp = await fetch("https://opentdb.com/api.php?amount=10&type=multiple")
            const questionsList = await resp.json()
            setQuestions(questionsList)
            const mappedQuestions = questionsList.map(question => {
                return{
                    ...question
                }
            })
          } catch (error) {
            alert(error)
          }
         }
        fetchData()  
      },[])


    const handleClick = ()=>{
        navigate("/trivia_game")
    }

    return(
        <div>
            <h1>Quiz Show</h1>
                <div>
                    <button className="pinkButton" onClick={handleClick}>
                        Start Trivia Game
                    </button>
                    <h2 className="score2">Your High Scores</h2>
                </div>
        </div>
    )
}

export default Welcome
